    <script>
        // --- Theme Toggling Logic ---
        const themeToggleBtn = document.getElementById('theme-toggle');
        const htmlElement = document.documentElement;
        const themeIcon = themeToggleBtn.querySelector('i');

        // Check local storage or system preference
        if (localStorage.theme === 'dark' || (!('theme' in localStorage) && window.matchMedia('(prefers-color-scheme: dark)').matches)) {
            htmlElement.classList.add('dark');
            themeIcon.classList.remove('fa-moon');
            themeIcon.classList.add('fa-sun');
        } else {
            htmlElement.classList.remove('dark');
            themeIcon.classList.remove('fa-sun');
            themeIcon.classList.add('fa-moon');
        }

        themeToggleBtn.addEventListener('click', () => {
            htmlElement.classList.toggle('dark');
            if (htmlElement.classList.contains('dark')) {
                localStorage.theme = 'dark';
                themeIcon.classList.remove('fa-moon');
                themeIcon.classList.add('fa-sun');
            } else {
                localStorage.theme = 'light';
                themeIcon.classList.remove('fa-sun');
                themeIcon.classList.add('fa-moon');
            }
        });

        // --- Interactive Playground Logic ---

        // 1. Light Toggle Visualization
        const lightToggle = document.getElementById('light-toggle');
        const lightStatus = document.getElementById('light-status');
        const vizCard = document.getElementById('viz-card');
        const vizCircle = document.getElementById('viz-circle');
        const vizIcon = document.getElementById('viz-icon');
        const vizTitle = document.getElementById('viz-title');
        const vizDesc = document.getElementById('viz-desc');
        const vizRing1 = document.getElementById('viz-ring-1');
        const vizRing2 = document.getElementById('viz-ring-2');

        lightToggle.addEventListener('change', (e) => {
            if(e.target.checked) {
                lightStatus.textContent = "System is currently Active";
                lightStatus.classList.add('text-primary');

                // Visual Updates
                vizCircle.classList.add('shadow-glow');
                vizCircle.style.boxShadow = "0 0 30px var(--primary-color)";
                vizIcon.classList.remove('text-textMuted');
                vizIcon.classList.add('text-primary');
                vizIcon.classList.remove('fa-power-off');
                vizIcon.classList.add('fa-bolt');

                vizTitle.textContent = "System Online";
                vizDesc.textContent = "Power output nominal. All systems green.";

                // Rings Animation
                vizRing1.classList.remove('opacity-0', 'scale-50');
                vizRing1.classList.add('opacity-100', 'scale-100');
                vizRing2.classList.remove('opacity-0', 'scale-50');
                vizRing2.classList.add('opacity-100', 'scale-100');

            } else {
                lightStatus.textContent = "System is currently Off";
                lightStatus.classList.remove('text-primary');

                // Visual Updates
                vizCircle.classList.remove('shadow-glow');
                vizCircle.style.boxShadow = "";
                vizIcon.classList.add('text-textMuted');
                vizIcon.classList.remove('text-primary');
                vizIcon.classList.add('fa-power-off');
                vizIcon.classList.remove('fa-bolt');

                vizTitle.textContent = "Standby Mode";
                vizDesc.textContent = "Activate the toggle to energize the system.";

                // Rings Animation
                vizRing1.classList.add('opacity-0', 'scale-50');
                vizRing1.classList.remove('opacity-100', 'scale-100');
                vizRing2.classList.add('opacity-0', 'scale-50');
                vizRing2.classList.remove('opacity-100', 'scale-100');
            }
        });

        // 2. Volume Slider
        const volumeSlider = document.getElementById('volume-slider');
        const volumeValue = document.getElementById('volume-value');

        volumeSlider.addEventListener('input', (e) => {
            const val = e.target.value;
            volumeValue.textContent = `${val}%`;

            // Dynamic color based on volume
            if(val > 80) {
                volumeValue.classList.remove('text-primary', 'text-yellow-500');
                volumeValue.classList.add('text-red-500');
            } else if (val > 40) {
                volumeValue.classList.remove('text-red-500', 'text-primary');
                volumeValue.classList.add('text-yellow-500');
            } else {
                volumeValue.classList.remove('text-red-500', 'text-yellow-500');
                volumeValue.classList.add('text-primary');
            }
        });

        // 3. Counter Logic
        let count = 0;
        const counterDisplay = document.getElementById('counter-value');

        function updateCounter(change) {
            count += change;
            // Simple animation effect
            counterDisplay.style.transform = "scale(1.2)";
            setTimeout(() => {
                counterDisplay.style.transform = "scale(1)";
            }, 150);
            counterDisplay.textContent = count;
        }

        // 4. Play Button Toggle
        const playBtn = document.getElementById('play-btn');
        let isPlaying = false;

        playBtn.addEventListener('click', () => {
            isPlaying = !isPlaying;
            const icon = playBtn.querySelector('i');
            if(isPlaying) {
                icon.classList.remove('fa-play');
                icon.classList.add('fa-pause');
                playBtn.classList.add('shadow-soft-inset');
                playBtn.classList.remove('shadow-soft');
            } else {
                icon.classList.remove('fa-pause');
                icon.classList.add('fa-play');
                playBtn.classList.remove('shadow-soft-inset');
                playBtn.classList.add('shadow-soft');
            }
        });

        // Navbar Scroll Effect
        window.addEventListener('scroll', () => {
            const nav = document.getElementById('navbar');
            if (window.scrollY > 50) {
                nav.classList.add('py-2');
                nav.classList.remove('py-4');
            } else {
                nav.classList.add('py-4');
                nav.classList.remove('py-2');
            }
        });

    </script>

</body></html>
