    </section>

    <!-- Contact Section -->
    <section id="contact" class="py-24 bg-cyber-dark relative">
        <div class="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
            <div class="bg-cyber-gray/30 border border-cyber-gray p-8 md:p-12 clip-corner relative overflow-hidden">
                <!-- Decorative BG code -->
                <div class="absolute top-0 right-0 p-4 text-6xl font-mono text-white opacity-5 select-none pointer-events-none">
                    010010<br>101101
                </div>

                <h2 class="text-3xl font-display font-bold text-white mb-2">ESTABLISH UPLINK</h2>
                <p class="text-gray-400 font-mono mb-8 text-sm">Encrypted transmission channel active. Awaiting input.</p>

                <form onsubmit="event.preventDefault(); simulateUpload();" class="space-y-6">
                    <div class="grid grid-cols-1 md:grid-cols-2 gap-6">
                        <div class="relative group">
                            <input type="text" id="name" required class="w-full bg-cyber-black border-b-2 border-cyber-gray text-white px-4 py-3 focus:outline-none focus:border-cyber-neonBlue transition-colors peer font-mono" placeholder=" ">
