    <script>
        // --- Mobile Menu Logic ---
        const btn = document.getElementById('mobile-menu-btn');
        const menu = document.getElementById('mobile-menu');

        btn.addEventListener('click', () => {
            menu.classList.toggle('hidden');
        });

        // --- Navbar Glass Effect on Scroll ---
        const navbar = document.getElementById('navbar');
        window.addEventListener('scroll', () => {
            if (window.scrollY > 50) {
                navbar.classList.add('bg-cyber-black/90', 'backdrop-blur-md', 'border-cyber-gray');
                navbar.classList.remove('border-transparent');
            } else {
                navbar.classList.remove('bg-cyber-black/90', 'backdrop-blur-md', 'border-cyber-gray');
                navbar.classList.add('border-transparent');
            }
        });

        // --- Text Scramble/Decode Effect ---
        const letters = "ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789_!@#$%^&*";

        document.querySelectorAll('.decode-text').forEach(element => {
            element.onmouseover = event => {
                let iteration = 0;
                const originalText = event.target.dataset.value;

                clearInterval(event.target.interval);

                event.target.interval = setInterval(() => {
                    event.target.innerText = originalText
                        .split("")
                        .map((letter, index) => {
                            if(index < iteration) {
                                return originalText[index];
                            }
                            return letters[Math.floor(Math.random() * 26)];
                        })
                        .join("");

                    if(iteration >= originalText.length){ 
                        clearInterval(event.target.interval);
                    }

                    iteration += 1 / 3;
                }, 30);
            }
        });

        // --- 3D Tilt Effect for Cards ---
        const cards = document.querySelectorAll('.cyber-card');

        cards.forEach(card => {
            card.addEventListener('mousemove', (e) => {
                const rect = card.getBoundingClientRect();
                const x = e.clientX - rect.left;
                const y = e.clientY - rect.top;

                const centerX = rect.width / 2;
                const centerY = rect.height / 2;

                const rotateX = ((y - centerY) / centerY) * -10; // Max rotation deg
                const rotateY = ((x - centerX) / centerX) * 10;

                card.style.transform = `perspective(1000px) rotateX(${rotateX}deg) rotateY(${rotateY}deg) scale3d(1.02, 1.02, 1.02)`;
            });

            card.addEventListener('mouseleave', () => {
                card.style.transform = 'perspective(1000px) rotateX(0) rotateY(0) scale3d(1, 1, 1)';
            });
        });

        // --- Magnetic Button Effect ---
        const magBtns = document.querySelectorAll('.magnetic-btn');
        magBtns.forEach(btn => {
            btn.addEventListener('mousemove', (e) => {
                const rect = btn.getBoundingClientRect();
                const x = e.clientX - rect.left - rect.width / 2;
                const y = e.clientY - rect.top - rect.height / 2;

                btn.style.transform = `translate(${x * 0.2}px, ${y * 0.2}px)`;
            });

            btn.addEventListener('mouseleave', () => {
                btn.style.transform = 'translate(0px, 0px)';
            });
        });

        // --- Modal Logic ---
        const modal = document.getElementById('modal');
        const modalContent = document.getElementById('modal-content');
        const modalTitle = document.getElementById('modal-title');
        const modalDesc = document.getElementById('modal-desc');

        function openModal(title, desc) {
            modalTitle.innerText = title;
            modalDesc.innerText = desc;
            modal.classList.remove('hidden');
            // Small delay to allow display:block to apply before opacity transition
            setTimeout(() => {
                modalContent.classList.remove('scale-95', 'opacity-0');
                modalContent.classList.add('scale-100', 'opacity-100');
            }, 10);
        }

        function closeModal() {
            modalContent.classList.remove('scale-100', 'opacity-100');
            modalContent.classList.add('scale-95', 'opacity-0');
            setTimeout(() => {
                modal.classList.add('hidden');
            }, 300);
        }

        // --- Form Simulation ---
        function simulateUpload() {
            const btn = document.querySelector('button[type="submit"]');
            const status = document.getElementById('status-msg');
            const barContainer = document.getElementById('progress-container');
            const bar = document.getElementById('progress-bar');

            btn.disabled = true;
            btn.innerText = "ENCRYPTING...";
            status.innerText = "UPLOADING DATA PACKET...";
            status.classList.remove('text-cyber-neonBlue');
            status.classList.add('text-cyber-neonPink');

            barContainer.classList.remove('hidden');

            // Force reflow
            void bar.offsetWidth; 
            bar.style.width = "100%";

            setTimeout(() => {
                status.innerText = "TRANSMISSION COMPLETE";
                status.classList.remove('text-cyber-neonPink');
                status.classList.add('text-cyber-neonBlue');
                btn.innerText = "DATA_SENT";

                setTimeout(() => {
                    // Reset
                    bar.style.width = "0";
                    barContainer.classList.add('hidden');
                    btn.disabled = false;
                    btn.innerText = "TRANSMIT_DATA";
                    document.querySelector('form').reset();
                }, 2000);
            }, 2200);
        }

        // --- Mouse Spotlight Effect ---
        const hero = document.getElementById('home');
        document.addEventListener('mousemove', (e) => {
            const x = e.clientX;
            const y = e.clientY;

            // Update CSS variables or apply gradient to body
            document.body.style.background = `radial-gradient(circle at ${x}px ${y}px, rgba(10, 10, 18, 0.8) 0%, #050505 60%)`;
        });

    </script>
</body>
</html>
